const db = {};
const saveData = (key, data, server) => {
  db[`${server ? server: ':cache:'} | ${key}`] = data;
};
const getData = (key,server) => {
  return db[`${server ? server: ':cache:'} | ${key}`]
};
const size = () => {
  let i = 0;
  for (const [k] of Object.entries(db)) {
    i++;
  }
  return i;
};
const all = () => db;
module.exports = { getData: getData, saveData: saveData, size: size, all: all };